import { Component, OnInit } from "@angular/core";
import { Product } from "src/app/common/product";
import { ProductService } from "src/app/services/product.service";

@Component({
  selector: "app-product-list",
  // templateUrl: "./product-list.component.html",
  // templateUrl: "./product-list.table.component.html",
  templateUrl: "./product-list-grid.component.html",
  styleUrls: ["./product-list.component.css"],
})
export class ProductListComponent implements OnInit {
  // property
  products: Product[] = [];

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.showListOfProducts();
  }

  showListOfProducts() {
    this.productService.getProductList().subscribe((data) => {
      this.products = data;
    });
  }
}
<table class="table">
  <thead class="table table-dark table-striped-columns">
    <tr>
      <th></th>
      <th>Name</th>
      <th>Price</th>
      <th>Units in Stock</th>
    </tr>
  </thead>

  <tbody>
    <tr *ngFor="let product of products">
      <td class="align-middle">
        <img src="{{ product.imageUrl }}" alt="placeholder" height="50" />
      </td>
      <td class="align-middle">{{ product.name }}</td>
      <td class="align-middle">{{ product.unitPrice | currency : "USD" }}</td>
      <td class="align-middle">{{ product.unitsInStock }}</td>
    </tr>
  </tbody>
</table>
